import React, { useState } from "react";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import TextField from "@material-ui/core/TextField";
import Typography from "@material-ui/core/Typography";
import axios from "axios";
export default function Login(props) {
  const [loginData, setloginData] = useState({
    email: "",
    password: "",
    name:""
  });
  const [loggedinData , setloggedinData] = useState('');
  const [signupData,setsignupData] = useState({
    data:"",flag:false
  })
  const [signupFlag, setsignupFlag] = useState(false);
  function handleChange(event) {
    const { name, value } = event.target;

    setloginData((prevData) => {
      return {
        ...prevData,
        [name]: value
      };
    });
  }
  function getGoogle(event) {
    // axios
    //   .get('/post')
    //   .then(() => console.log('Resume Created'))
    //   .catch(err => {
    //     console.error(err);
    //   });
  }
  function signupPage(event) {
    event.preventDefault();
    setsignupFlag(!signupFlag);
    setloginData({
      email: "",
      password: ""
    });
  }
  function signupUser(event) {
    event.preventDefault();
    setsignupFlag(false);
    const {email,password}=loginData;
    const id = Math.floor(Math.random()*100000);
    const body={id,email,password};
    axios
    .post('/signup', body)
    .then(res => {setsignupData(() => {
      return {
        data:res.data.userId?res.data.userId:res.data.err,
      flag:res.data.flag}})})
    .catch(err => {
      console.error(err);
    });
    setloginData({
      email: "",
      password: ""
    });
  }
  async function signupverify(event) {
    event.preventDefault();
    const {email,password}=loginData;
    const body={email,password};
    let response;
    await axios
    .post('/loginUser', body)
    .then(res => {setloggedinData(res.data.userId);})
    .catch(err => {
      console.error(err);
    });
    console.log(loggedinData);
    //props.authenticate(true);
  }
  return (
    <div className="row">
      <div className="col-lg-8 col-sm-8 col-xs-8 loginForm">
        <div className="card ml-5">
          <div className="card-body">
            <form method="POST" noValidate autoComplete="off">
              {signupFlag ? (
                <Typography>Sign up</Typography>
              ) : (
                <Typography>Login</Typography>
              )}
              {signupData.flag?(<div class="alert alert-success" role="alert">
  User signed in Sucessfully!
</div>):(
<div class="alert alert-danger" role="alert">
  {signupData.data}
</div>)}
              <div className="row">
                <div className="col-lg-8">
                  <TextField
                    name="email"
                    id="email"
                    label="Email ID"
                    variant="outlined"
                    type="email"
                    value={loginData.email}
                    onChange={handleChange}
                    required
                  />
                </div>
                <div className="col-lg-8">
                  <TextField
                    name="password"
                    id="password"
                    label="Password"
                    variant="outlined"
                    type="password"
                    value={loginData.password}
                    onChange={handleChange}
                    required
                  />
                </div>
              </div>
              {signupFlag ? (
                <div>
                <button className="btn btn-primary mr-2" onClick={signupUser}>
                  Sign up
                </button>
                <button className="btn btn-dark mr-2" onClick={signupPage}>
                Click for Login
                </button>
                </div>
              ) : (
                <div>
                  <button className="btn btn-dark mr-2" onClick={signupPage}>
                    Click for Sign up
                  </button>

                  <button className="btn btn-primary" onClick={signupverify}>
                    Login
                  </button>
                </div>
              )}
            </form>
          </div>
        </div>
      </div>
      <div className="col-lg-4 col-sm-4 col-xs-4 socialMedBtn">
        <Card>
          <CardContent>
            <div>
              <a
                className="btn btn-block btn-social btn-google"
                href="/auth/google"
                role="button"
              >
                <i className="fab fa-google"></i>
                Sign Up with Google
              </a>
            </div>
            <div className="mt-2">
              <a
                className="btn btn-block btn-social btn-facebook"
                href="/auth/facebook"
                role="button"
              >
                <i className="fab fa-facebook"></i>
                Sign Up with Facebook
              </a>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}
